"use strict";$(function(){$.get("./src/hotlist.json").then(function(n){var i=n,s=$("<ol></ol>"),o=$(".playlist");i.forEach(function(n,i){$li=$('\n          <li>\n            <h3 class="order-number">'+(i+1)+'</h3>\n            <div class="song">\n              <h3 class="song-name">'+n.songName+'</h3>\n              <p class="song-info">  \n                '+n.songAuthor+" - "+n.album+'\n              </p>\n              <a class="play-btn" href="#">\n                  <svg class="icon" aria-hidden="true">\n                    <use xlink:href="#icon-play"></use>\n                  </svg>\n              </a>\n            </div>\n          </li>\n        '),!0===n.sq&&$li.find(".song-info").prepend($('\n            <svg class="icon sq" aria-hidden="true">\n              <use xlink:href="#icon-sq"></use>\n            </svg>\n          ')),s.append($li)}),o.append(s).find(".loading").remove(),s.on("click","li",function(){var n=$(this).index();window.location.href="./play.html?id="+n})});var n=$(".brief-info");n.on("click",function(i){n.toggleClass("active")})});